import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  public mensaje: string = "Home Component";
  public fecha: Date = new Date();
  public counter: number  = 100;

  incrementar(valor: number):void {
      this.counter += valor;
  }

  reiniciar():void {
      this.counter = 100;
  }
}
<p>home works!</p>
<hr>

<h3> {{counter}}</h3>
<h2>Fecha de hoy: {{fecha | date: 'short'}}</h2> 
<p>{{mensaje | miPipe}}</p>
<button (click)="incrementar(+1)" >+1</button>
<button (click)="incrementar(-1)" >-1</button>
<button (click)="reiniciar()">Reset</button>
<hr>
