import { Component, OnInit } from '@angular/core';
import { ServicioService } from "../app/services/servicio.service";
import { PublicoService } from "../app/services/publico.service";
//import {  } from "./models/estudiante-asignado-interface";
import { UsuarioInterface } from "../app/models/usuario-interface";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'Bienvenido!';

  constructor(private servicio:PublicoService, private actividadService:ServicioService){}
  registroUsuario:any = null; //this.servicio.getValor();
  idUsuario:any = null;
  usuario?:UsuarioInterface;

  ngOnInit(): void {

  };

  login():void {
    this.servicio.setRegistro(); //Estableciendo registro en el servicio público
    this.registroUsuario = this.servicio.getRegistro(); //Obteniendo registro del servicio

    //***Crear un objeto de tipo usuario, ya que devolverá toda esa información (similar al objeto actividad)
    //REVISAR EL OBJETO QUE DEVUELVE POSTMAN EN core_user_get_users_by_field
    //this.idUsuario = this.actividadService.getUsuario(this.registroUsuario); //Se manda el registro para luego obtener el id en moodle
    this.actividadService.getUsuario('e'+this.registroUsuario).subscribe((res)=>{
      console.log(res);
      this.usuario = res as UsuarioInterface;
      console.log("Imprimiendo datos del usuario logueado - " + this.registroUsuario);
      console.log(this.usuario.id);
    })
    //this.servicio.setValor(this.valor);
  }

  logout():void {
    this.registroUsuario = null;
    //this.servicio.setValor(this.valor);

  }
}
<!--<h1>División de Desarrollo Organizacional</h1>-->

<nav class="navbar bg-body-tertiary">
  <div class="container">
    <a class="navbar-brand" href="#">
      <img src="https://ddo.usac.edu.gt/wp-content/uploads/2024/03/cropped-dissddo2022_Mesa-de-trabajo-1.png" class="img-fluid" alt="...">
    </a>
  </div>
</nav>

<nav class="navbar bg-body-tertiary">
  <div class="container-fluid">
    <div class="text-end w-100">
      <h2 class="modal-title bg-body-tertiary ">{{registroUsuario }}</h2>
    </div>
  </div>
</nav>


<nav class="navbar navbar-expand-lg bg-body-tertiary">
  <div class="container-fluid">
    <a class="navbar-brand" >DDO</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarScroll" aria-controls="navbarScroll" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarScroll">
      <ul 
        *ngIf="registroUsuario !== null"
        class="navbar-nav me-auto my-2 my-lg-0 navbar-nav-scroll" style="--bs-scroll-height: 100px;">
        <li class="nav-item">
          <!--<a class="nav-link" routerLink="">Inicio</a>-->
          <a class="nav-link" routerLink="misactividades">Mis actividades</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="admin">Administrador</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="actividades">Actividades</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="usuarios">Usuarios</a>
        </li>
        <!--<li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Link
          </a>
          <ul class="dropdown-menu">
            <li><a class="dropdown-item" href="#">Action</a></li>
            <li><a class="dropdown-item" href="#">Another action</a></li>
            <li><hr class="dropdown-divider"></li>
            <li><a class="dropdown-item" href="#">Something else here</a></li>
          </ul>
        </li>-->
        <li class="nav-item">
          <!--<a class="nav-link disabled" aria-disabled="true">Link</a>-->
        </li>

      </ul>
      <!--
      <form class="d-flex" role="search">
        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search">
        <button class="btn btn-outline-success" type="submit">Search</button>
      </form>
      -->

      <!-- ms-auto agrega un margen automático a la izquierda del botón, empujándolo hacia el extremo derecho del contenedor.
          me-2 Agrega un margen derecho pequeño (0.5 rem por defecto) al botón 
    -->
      <button 
        *ngIf="registroUsuario === null"
        (click)="login()"
        class="btn btn-outline-success ms-auto" type="button"> 
        Iniciar sesión
      </button>
      <button             
        *ngIf="registroUsuario !== null"
        (click)="logout()"
        class="btn btn-outline-danger" type="button">
        Cerrar sesión
      </button>


    </div>
  </div>
</nav>


