import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-detalle-actividad',
  templateUrl: './detalle-actividad.component.html',
  styleUrls: ['./detalle-actividad.component.css']
})
export class DetalleActividadComponent implements OnInit {
  actividad: string = '';
  constructor(private _route: ActivatedRoute) {}

  ngOnInit(): void {
    /*this._route.params.subscribe(params => {
      this.actividad = params['actividadId'];
    });*/

    const id = this._route.snapshot.paramMap.get('actividadId');
    if(id){
      this.actividad = `${id}`;
    }
  }
}
