import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';

import { ServicioService } from "../../services/servicio.service";
import { PublicoService } from "../../services/publico.service";
import { UsuarioInterface } from "../../models/usuario-interface";


@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit{

  usuarios:UsuarioInterface[] = [];

  constructor(private servicio: PublicoService, private actividadService: ServicioService, private route: ActivatedRoute, private location: Location) {}

  ngOnInit(): void {
    this.actividadService.getUsuarios().subscribe({
      next:(res)=>{
        this.usuarios = res as UsuarioInterface[];
        console.log("Se obtuvo correctamente el listado de estudiantes ");
      },
      error:(err)=> {
          console.error("Error al obtener el listado de estudiantes ", err);
        }
    })    
  }

  volver(): void {
    this.location.back(); //
  }

}
<div class="container mt-3">
    <div class="row mt-5 justify-content-center">

        <div class="col-6">
            <h2>LISTADO DE USUARIOS: </h2>
            <br>
            <form>
              <button (click)="volver()" type="submit" class="btn btn-secondary btn-block">Regresar</button>
              <br>
              <br>

                <table class="table">
                    <thead>
                      <tr>
                        <th scope="col">Registro</th>
                        <th scope="col">Nombre</th>
                        <th scope="col">Apellido</th>
                        <th scope="col">Correo</th>
                      </tr>
                    </thead>
                    <tbody class="table-group-divider">
                      <tr *ngFor="let estudiante of usuarios">
                        <th scope="row">{{estudiante.username}}</th>
                        <td>{{estudiante.firstname}}</td>
                        <td>{{estudiante.lastname}} </td>
                        <td>{{estudiante.email}} </td>
                      </tr>
                    </tbody>
                  </table>

                <br>

            </form>
        </div>

    </div>
</div>
