<div class="container ">

  <div class="row justify-content-center">
    <div class="col-9">
        <div class="row justify-content-center">

          <div class="col-5">
            <form class="d-flex mb-4" role="search">
              <input class="form-control  me-2" type="search" [(ngModel)]="terminoBusqueda" placeholder="Buscar" aria-label="Search">
              <button class="btn btn-outline-success" type="submit" (click)="buscarActividad()">Buscar</button>
              <button class="btn btn-outline-success" type="submit" (click)="limpiarBusqueda()">Limpiar</button>
            </form>
          </div>

          <div class="w-100 mb-4">
            <hr>
          </div>

          <div *ngIf="encontrado" class="col-4">
              <div class="card bg-light mb-3 border border-secondary">
                  <div class="card-header">Nombre: {{actividadEncontrada?.fullname}}</div>
                      <!--<app-detalle [nombre]="producto.nombre"> </app-detalle>-->
