                      <!--<img src="../assets/productos/{{producto.foto}}" class="card-img-top" alt="...">-->
                      <h5 class="card-title">Tipo de actividad: {{getNombreCategoria(actividadEncontrada?.categoryid)}}</h5>
                      <!--<h5 class="card-title">Docente asignado: {{actividad.id_personaAdmin}}</h5>-->
                  </div>
                  <div class="card-footer">
                      <div class="d-flex justify-content-center">
                          <button type="submit"
                              [routerLink]="['/actividad', actividadEncontrada?.id]"
                              class="btn btn-warning btn-block">Ver detalle</button>
                      </div>
                  </div>
              </div>
          </div>


            <div *ngFor="let actividad of actividades" class="col-4"> <!--Productos (home.component.ts)-->
              <div *ngIf="!encontrado" class="card bg-light mb-3 border border-secondary">
                  <div class="card-header">Nombre: {{actividad.fullname}}</div>
                      <!--<app-detalle [nombre]="producto.nombre"> </app-detalle>-->
                  <div class="card-body">
                      <h5 class="card-title">Descripción: {{actividad.shortname}}</h5>
                      <!--<img src="../assets/productos/{{producto.foto}}" class="card-img-top" alt="...">-->
                      <h5 class="card-title">Tipo de actividad: {{getNombreCategoria(actividad.categoryid)}}</h5>
                      <!--<h5 class="card-title">Docente asignado: {{actividad.id_personaAdmin}}</h5>-->
                  </div>
                  <div class="card-footer">
                      <div class="d-flex justify-content-center">
                          <button type="submit"
                              [routerLink]="['/actividad', actividad.id]"
                              class="btn btn-warning btn-block">Ver detalle</button>
                      </div>
                  </div>
              </div>
            </div>



        </div>
    </div>
  </div>
</div>
