import Keycloak from 'keycloak-js';
import { KeycloakService } from 'keycloak-angular';

export function initializeKeycloak(keycloak: KeycloakService): () => Promise<boolean> {
  return (): Promise<boolean> => {
    return keycloak
      .init({
        config: {
          url: 'http://localhost:8080', // URL de tu servidor Keycloak
          realm: 'ddo',                 // Nombre de tu realm
          clientId: 'cliente_nodejs',          // ID de tu cliente registrado en Keycloak
        },
        initOptions: {
          onLoad: 'login-required',         // Carga y fuerza el inicio de sesi칩n
          checkLoginIframe: true,          // Revisa la sesi칩n activa en el iframe
        },
        enableBearerInterceptor: true,      // Interceptor para a침adir el token a las solicitudes HTTP
        bearerExcludedUrls: ['/assets'],    // URLs excluidas del token
      })
      .then(() => {
        console.log('Keycloak inicializado correctamente');
        return true;
      })
      .catch((error) => {
        console.error('Error al inicializar Keycloak:', error);
        return false;
      });
  };
}
