import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class PublicoService {

  private registroUsuario:any; // Variable para almacenar el registro académico
  private idUsuario:any; //Variable para almacenar el id del usuario en moodle
  private indice: number;
  registros: string[] = [
    /*'20030361',
    '20060102',
    '20171111',*/
    't20090388',
    'e201602560',
    /*'20200391',
    '20100086',
    '20160311',
    '20020819',
    '19990735',
    '20101934'*/
  ];

  constructor() { 
    this.registroUsuario = null;
    this.idUsuario = null;
    this.indice = 0;
  }

  getRegistro(): any {
    return this.registroUsuario;
  }

  setRegistro():void {
    this.indice = Math.floor(Math.random() * 2);
    this.registroUsuario = this.registros[this.indice];
  }

  setValor(valor:any):void {
    this.registroUsuario = valor;
  }

  getIdUsuario():any{
    return this.idUsuario;
  }

  setIdUsuario(valror:any){
    this.idUsuario = valror;
  }
}
