import { Component, OnInit } from '@angular/core';
import { ServicioService } from "../../services/servicio.service";
import { ActividadInterface } from "../../models/actividad-interface";

@Component({
  selector: 'app-actividades',
  templateUrl: './actividades.component.html',
  styleUrls: ['./actividades.component.css']
})
export class ActividadesComponent implements OnInit{

  id:string = "";
  nombre:string = "";
  descripcion:string = "";
  //actividades:Object=[];

  actividades:ActividadInterface[] = [];

  constructor(public actividadService: ServicioService){}

  ngOnInit(): void {
    this.actividadService.getActividades().subscribe((res) => {
      console.log(res);
      console.log(typeof(res));
      this.actividades = res as ActividadInterface[];
      console.log(this.actividades[4].nombre);
      console.log(this.actividades.length);
    });
  }

}
<p>Área de Actividades</p>

<div class="container">

    <table class="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">First</th>
            <th scope="col">Last</th>
            <th scope="col">Detalle</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th scope="row">1</th>
            <td>Mark</td>
            <td>Otto</td>
            <button type="button" class="btn btn-outline-primary" [routerLink]="['/actividades', 'orga']">Detalle</button>
            <!-- [routerLink="['actividades', actividad.id]"] -->
          </tr>
          <tr>
            <th scope="row">2</th>
            <td>Jacob</td>
            <td>Thornton</td>
            <button type="button" class="btn btn-outline-primary" [routerLink]="['/actividades', 'edd']">Detalle</button>
          </tr>
          <tr>
            <th scope="row">3</th>
            <td colspan="2">Larry the Bird</td>
            <button type="button" class="btn btn-outline-primary" [routerLink]="['/actividades', 'sa']">Detalle</button>
          </tr>
        </tbody>
    </table>

</div>
