import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ServicioService } from "../../services/servicio.service";
import { ActividadInterface } from "../../models/actividad-interface";

@Component({
  selector: 'app-detalle-actividad',
  templateUrl: './detalle-actividad.component.html',
  styleUrls: ['./detalle-actividad.component.css']
})
export class DetalleActividadComponent implements OnInit {
  parametro: string = '';

  id_actividad?: number;
  nombre?: string;
  descripcion?: string;
  fecha_inicio?: Date;
  fecha_fin?: Date;
  id_tipoActividad?: number;
  id_personaAdmin?: number;
  //actividades:Object=[];
  actividad?: ActividadInterface;

  constructor(public actividadService: ServicioService, private _route: ActivatedRoute) {}

  ngOnInit(): void {
    /*this._route.params.subscribe(params => {
      this.actividad = params['actividadId'];
    });*/

    const id = this._route.snapshot.paramMap.get('idActividad');
    if(id){
      this.parametro = `${id}`;
    }
    this.id_actividad = parseInt(this.parametro, 10); //Convertir a entenro, base decimal (10)

    this.actividadService.getActividad(this.id_actividad).subscribe((res)=>{
      console.log(res); //La respuesta en un objeto JSON, proveniente del backend
      this.actividad = res as ActividadInterface;
    });

  }
}
