import { Component, OnInit } from '@angular/core';
import { ServicioService } from "../../services/servicio.service";
import { UsuarioInterface } from "../../models/usuario-interface";

@Component({
  selector: 'app-usuarios',
  templateUrl: './usuarios.component.html',
  styleUrls: ['./usuarios.component.css']
})
export class UsuariosComponent implements OnInit {

  usuarios:UsuarioInterface[] = [];

  constructor(public actividadService: ServicioService){}

  ngOnInit(): void {
    this.actividadService.getUsuarios().subscribe((res) => {
      console.log(res);
      //console.log(typeof(res));
      this.usuarios = res as UsuarioInterface[];
      //console.log(this.actividades[4].nombre);
      //console.log(this.actividades.length);
    });
  }

}
<div class="container mt-3">
    <div class="row mt-5 justify-content-center">

        <div class="col-6">
            <h2>LISTADO DE USUARIOS: </h2>
            <br>
            <form>
                
                <table class="table">
                    <thead>
                      <tr>
                        <th scope="col">Registro</th>
                        <th scope="col">Nombre</th>
                        <th scope="col">Apellido</th>
                        <th scope="col">Correo</th>
                      </tr>
                    </thead>
                    <tbody class="table-group-divider">
                      <tr *ngFor="let usuario of usuarios">
                        <th scope="row">{{usuario.username}}</th>
                        <td>{{usuario.firstname}}</td>
                        <td>{{usuario.lastaccess}} </td>
                        <td>{{usuario.email}} </td>
                      </tr>
                    </tbody>
                  </table>


                <br>
                <button       type="submit" class="btn btn-primary btn-block">Regresar</button>

            </form>
        </div>


    </div>
</div>
